vg-volume {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    display: flex;
    justify-content: center;
    height: 50px;
    width: 100px;
    cursor: pointer;
    color: white;
    line-height: 50px;
}
vg-volume .volumeBar {
    position: relative;
    display: flex;
    flex-grow: 1;
    align-items: center;
}
vg-volume .volumeBackground {
    display: flex;
    flex-grow: 1;
    height: 5px;
    pointer-events: none;
    background-color: #333;
}
vg-volume .volumeValue {
    display: flex;
    height: 5px;
    pointer-events: none;
    background-color: #FFF;
    transition:all 0.2s ease-out;
}
vg-volume .volumeKnob {
    position: absolute;
    width: 15px; height: 15px;
    left: 0; top: 50%;
    transform: translateY(-50%);
    border-radius: 15px;
    pointer-events: none;
    background-color: #FFF;
    transition:all 0.2s ease-out;
}
vg-volume .volumeBackground.dragging .volumeValue,
vg-volume .volumeBackground.dragging .volumeKnob {
    transition: none;
}

vg-play-pause {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
