}

vg-scrub-bar {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    position: absolute;
    width: 100%;
    height: 5px;
    bottom: 50px;
    margin: 0;
    cursor: pointer;
    align-items: center;
    background: rgba(0, 0, 0, 0.75);
    z-index: 250;
    -webkit-transition: bottom 1s, opacity 0.5s;
    -khtml-transition: bottom 1s, opacity 0.5s;
    -moz-transition: bottom 1s, opacity 0.5s;
    -ms-transition: bottom 1s, opacity 0.5s;
    transition: bottom 1s, opacity 0.5s;
}
vg-scrub-bar .scrubBar {
    position: relative;
    display: flex;
    flex-grow: 1;
    align-items: center;
    height: 100%;
}
vg-controls vg-scrub-bar {
    position: relative;
    bottom: 0;
    background: transparent;
    height: 50px;
    flex-grow: 1;
    flex-basis: 0;
    margin: 0 10px;
    -webkit-transition: initial;
    -khtml-transition: initial;
    -moz-transition: initial;
    -ms-transition: initial;
    transition: initial;
}
vg-scrub-bar.hide {
    bottom: 0;
    opacity: 0;
}
vg-controls vg-scrub-bar.hide {
    bottom: initial;
    opacity: initial;
}

vg-scrub-bar-buffering-time {
    display: flex;
    width: 100%;
    height: 5px;
    pointer-events: none;
    position: absolute;
}
vg-scrub-bar-buffering-time .background {
    background-color: rgba(255, 255, 255, 0.3);
}
vg-controls vg-scrub-bar-buffering-time {
    position: absolute;
    top: calc(50% - 3px);
}
vg-controls vg-scrub-bar-buffering-time .background {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}

vg-scrub-bar-cue-points {
    display: flex;
    width: 100%;
    height: 5px;
    pointer-events: none;
    position: absolute;
}
vg-scrub-bar-cue-points .cue-point-container .cue-point {
    position: absolute;
    height: 5px;
    background-color: rgba(255, 204, 0, 0.7);
}
vg-controls vg-scrub-bar-cue-points {
    position: absolute;
    top: calc(50% - 3px);
}

vg-scrub-bar-current-time {
    display: flex;
    width: 100%;
    height: 5px;
    pointer-events: none;
    position: absolute;
}
vg-scrub-bar-current-time .background {
    background-color: white;
}
vg-controls vg-scrub-bar-current-time {
    position: absolute;
    top: calc(50% - 3px);
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
vg-controls vg-scrub-bar-current-time .background {
    border: 1px solid white;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}

vg-scrub-bar-current-time .slider{
    background: white;
    height: 15px;
    width: 15px;
    border-radius: 50%;
    box-shadow: 0px 0px 10px black;
    margin-top: -5px;
    margin-left: -10px;
}

vg-time-display {
    -webkit-touch-callout: none;
