vg-player {
    font-family: 'videogular';
    position: relative;
    display: flex;
    width: 100%;
    height: 100%;
    overflow: hidden;
    background-color: black;
}
vg-player.fullscreen {
    position: fixed;
    left: 0;
    top: 0;
}
vg-player.native-fullscreen.controls-hidden {
    cursor: none;
}

vg-ima-ads {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 300;
}
vg-ima-ads .vg-ima-ads {
    position: absolute;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

vg-overlay-play {
    z-index: 200;
}
vg-overlay-play.is-buffering {
    display: none;
}
vg-overlay-play .vg-overlay-play {
    transition: all 0.5s;
    cursor: pointer;
    position: absolute;
    display: block;
    color: white;
    width: 100%;
    height: 100%;
    font-size: 80px;
    filter: alpha(opacity=60);
    opacity: 0.6;
}
vg-overlay-play .vg-overlay-play.native-fullscreen.controls-hidden {
    cursor: none;
}
vg-overlay-play .vg-overlay-play .overlay-play-container.vg-icon-play_arrow {
    pointer-events: none;
    width: 100%;
    height: 100%;
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 80px;
}
vg-overlay-play .vg-overlay-play:hover {
    filter: alpha(opacity=100);
    opacity: 1;
}
vg-overlay-play .vg-overlay-play:hover .overlay-play-container.vg-icon-play_arrow:before {
    transform: scale(1.2);
}
